import React, { Component } from "react";
import style from "./RowSeat.module.css";

export default class RowSeat extends Component {
  render() {
    let inforRow = this.props.inforRow;

    const renderSeat = () => {
      return inforRow.danhSachGhe.map((item, index) => {
        return (
          <button className={`${style["seat"]} btn`} key={item.soGhe}>
            {index + 1}
          </button>
        );
      });
    };

    const renderRowSeat = () => {
      return (
        <div className={`${style["row-seat"]}`}>
          <span className={`${style["row-seat__name"]}`}>{inforRow.hang}</span>
          {renderSeat()}
        </div>
      );
    };

    return <div>{renderRowSeat()}</div>;
  }
}
