import React, { Component } from "react";
import style from "./RowSeat.module.css";

export default class RowSeat extends Component {
  render() {
    let props = this.props;
    console.log(props);
    const renderSeat = () => {
      return props.inforRow.danhSachGhe.map((item, index) => {
        return (
          <span
            key={item.soGhe}
            className={`${style.seat} col-1 text-white bgColor-red`}
          >
            {index + 1}
          </span>
        );
      });
    };
    const render = () => {
      return (
        <div className="row">
          <div className="col-1 text-white">{props.inforRow.hang}</div>
          <div className="col-11">
            <div className="row">{renderSeat()}</div>
          </div>
        </div>
      );
    };
    return <div>{render()}</div>;
  }
}
