import React, { Component } from "react";
import { connect } from "react-redux";

class Seat extends Component {
  render() {
    console.log(this.props.seat);
    const renderRowSeat = (arr) => {
      return arr.map((el, index) => {
        return <button>{el.soGhe}</button>;
      });
    };

    const renderSeat = (arr) => {
      let result = arr.map((element, index) => {
        return (
          <div key={index}>
            {element.hang}
            {renderRowSeat(element.danhSachGhe)}
          </div>
        );
      });
      return result;
    };

    let arrSeat = this.props.seat;
    return <React.Fragment>{renderSeat(arrSeat)}</React.Fragment>;
  }
}

const mapStateToProps = (rootReducer) => {
  return {
    seat: rootReducer.seatReducer,
  };
};

export default connect(mapStateToProps)(Seat);
